// query: distância em km (string ou number)
let distance = $input.first().json.distance;
const dist = typeof distance === 'string'
  ? parseFloat(distance.replace(',', '.'))
  : distance;

if (isNaN(dist) || dist < 0) {
  throw new Error(`Distância inválida: ${distance}`);
}

let frete;
if (dist <= 3) {
  frete = 5.00;
} else if (dist <= 6) {
  frete = 8.00;
} else if (dist <= 10) {
  frete = 12.00;
} else {
  // acima de 10 km, R$ 12 + R$ 1 por km adicional
  frete = parseFloat((12 + (dist - 10) * 1).toFixed(2));
}

return {
    "frete" : frete}
  ;
